        room["sorted"] = sorted;
    }

    if (!room.hasOwnProperty("scheduleName")){
        node.warn(`The room has no name. Details: ${room}\nThe room is now called NoNameRoom`);
        room["scheduleName"] = "NoNameRoom";
    }

    if (!room.hasOwnProperty("url") || !room.hasOwnProperty("scheduleID")) {
        node.error(`The room ${room.name} has no url or scheduleID`);
