let setTempOccup = flow.get('$parent.f_tempOccupied');
let setTempUnOccup = flow.get('$parent.f_tempUnOccupied');
let timeZone = flow.get('$parent.f_timeZone');
let timeOffsetBeforeStart = flow.get('$parent.f_timeOffsetBeforeStart');
let timeOffsetBeforeEnd = flow.get('$parent.f_timeOffsetBeforeEnd');
let dailySchedule = flow.get('$parent.f_dailySchedule');

// DO NOT MODIFY
let length = msg.total;
let jsonObj = {};
let events = [];

if (msg.error) {
    node.error(msg.error.name + ': ' + msg.error.message + '. Impossible to process the calendar.');
    return null;
}

// Catch all the events
for (let i = 0; i < length; i++) {

    // Get event information
    let name = msg.payload[i].summary;
    let description = msg.payload[i].description;
    let eventStart = new Date(msg.payload[i].eventStart);
    let eventEnd = new Date(msg.payload[i].eventEnd);
    let contentRmEvent = flow.get('$parent.f_contentRmEvent');
    let eventAlreadyExists = false;

    if (flow.get('$parent.f_dailySchedule')) {
        if (contentRmEvent.some(element => description.includes(element))) eventAlreadyExists = true; // Skip SOBRIETE ENERGETIQUE events
        else {
            let start = 0;
            if (msg.sorted) {
                start = events.length - 1; // Last event should be the previous one
            }
            for (let j = start; j < events.length; j++) {
                if (eventStart.getDate() == events[j].start.getDate() &&
                    eventStart.getMonth() == events[j].start.getMonth() &&
                    eventStart.getFullYear() == events[j].start.getFullYear()) {

                    // Group same day events into only one event
                    if (eventStart < events[j].start) events[j].start = eventStart;
                    if (eventEnd > events[j].end) events[j].end = eventEnd;
                    eventAlreadyExists = true;
                    events[j].name += ' AND ' + name;
                }
            }
        }

    }

    if (!eventAlreadyExists) {
        events.push({
            name: name,
            start: eventStart,
            end: eventEnd
        })
    };
};

// Store the events and payload build
for (let j = 0; j < events.length; j++) {
    let name = events[j].name;
    let day = events[j].start.getDate();
    let month = events[j].start.getMonth() + 1; // January gives 0
    let year = events[j].start.getFullYear();
    let start;
    let end;

    // Add offset for 1 event/day schedule
    if (dailySchedule) {
        start = new Date(events[j].start.valueOf() - timeOffsetBeforeStart * 60000).toLocaleTimeString("fr-FR", { timeZone: timeZone });
        end = new Date(events[j].end.valueOf() - timeOffsetBeforeEnd * 60000).toLocaleTimeString("fr-FR", { timeZone: timeZone });
    }
    else {
        start = events[j].start.toLocaleTimeString("fr-FR", { timeZone: timeZone });
        end = events[j].end.toLocaleTimeString("fr-FR", { timeZone: timeZone });
    }

    // Creating string
    let message = `{ "${Number(j + 1)}": {
        "event-priority": 16, 
        "period": { 
            "date": {
                "month": ${month}, 
                "year": ${year}, 
                "day-of-month": ${day}
            }, 
            "type": "Date" 
        }, 
        "name": "${name}", 
        "transitions": { 
            "1": { "time": "${start}", "value": ${setTempOccup}, "key": "1" },  
            "2": { "time": "${end}", "value": null, "key": "2" } 
        }, 
        "key": "${Number(j + 1)}" 
    }}`;

    let jsonTemp = JSON.parse(message);     // Transform string into an object
    jsonObj = { ...jsonObj, ...jsonTemp };   // Merge with the previous object
};

let request = {
    "method": "POST",
    "url": `https://${flow.get('$parent.f_controllerIP')}/api/rest/v2/services/bacnet/local/objects/schedules/${msg.ScheduleID}/special-events`,
    "headers": {
        Authorization: flow.get('$parent.f_akey'),
        ContentType: "application/json"
    },
    "payload": jsonObj,
    "roomInfo": msg
};

return request;

