// Controller information
const controllerIP = "TO_CONFIGURE";       // Controller IP Address
const controllerLogin = "TO_CONFIGURE";            //Apex distech login
const controllerPassword = "TO_CONFIGURE";  //Apex distech password

// Schedule object information
const timeZone = 'Europe/Paris';
const dailySchedule = false;                 // Group events in 1 event/day
const timeOffsetBeforeStart = 0;            // in min
const timeOffsetBeforeEnd = 0;              // in min
const contentRmEvent = ["TO_CONFIGURE"];      // if DAILY_SCHEDULE is enable, 
                                                    // all events that content this 
                                                    // string in their description 
                                                    // will be ignored
const preview = 7;
const pastview = 0;
const sorted = false;                       // Events are sorted in chronological order.  
                                            // This helps to reduce algorithm complexity 
                                            // when enable

// Schedule object values
const tempOccupied = 20;
const tempUnOccupied = 17;

//  binding values /!\Not useful to configure if binding is not used
const generateScheduleAnalogValues = false; //  If enabled will generate an analog value for each schedule
const schedulesAnalogValuesOffset = 10000;  //  The starting instance of theses values
const deviceInstanceOffset = 0; // Device Offset configured in LoRaBAC
const deviceInstanceRange = 10; // Instance range of analog values object of the device configured in LoRaBAC
const deviceObjectInstanceNum = 2; // Instance offset of the analog values object of the device you want to bind with the schedule configured in LoRaBAC



// Room template
let template = {
    "ScheduleID": 0,    //To use the link correctly make sure to use following numbers and start with 0
    "name": "roomName",
    "devices": [0, 1, 2], //device number of LoRaBAC
    "url": 'room_url'
}

// Add your own rooms
let rooms = [
    {
        "ScheduleID": 0,
        "name": "test",
        "devices": [1],
        "url": "https://calendar.google.com/calendar/ical/7f40863311d83d9ef22d9ccfddf3fa32fa7669054f85f880d255caee81254114%40group.calendar.google.com/private-ec1cc71468607f7b01ca829832811b53/basic.ics",
    }
];

/////////////////////////////////////////////////
/////////////// DO NOT MODIFY ///////////////////
/////////////////////////////////////////////////
for (let r in rooms) {
    const room = rooms[r]
    // Add non mandatory information
    if (!room.hasOwnProperty("preview")){
        room["preview"] = flow.get('g_preview');
    }

    if (!room.hasOwnProperty("pastview")){
        room["pastview"] = flow.get('g_pastview');
    }

    if (!room.hasOwnProperty("timezone")){
        room["timezone"] = flow.get('g_timeZone');
    }

    if (!room.hasOwnProperty("sorted")){
        room["sorted"] = flow.get('g_sorted');
    }

    if (!room.hasOwnProperty("name")){
        node.warn(`The room has no name. Details: ${room}\nThe room is now called NoNameRoom`);
        room["name"] = "NoNameRoom";
    }

    if (!room.hasOwnProperty("url") || !room.hasOwnProperty("ScheduleID")) {
        node.error(`The room ${room.name} has no url or ScheduleID`);
        node.status({fill:"red",shape:"dot",text:`The room ${room.name} has no url or ScheduleID`});
        return null;
    }

}

//Autentification key
let bufferkey = Buffer.from(controllerLogin + ":" + controllerPassword);
const keybase64 = bufferkey.toString('base64');
const autentikey = "Basic " + keybase64;

// Store variables
flow.set('f_controllerIP', controllerIP);
flow.set('f_akey', autentikey);

flow.set('f_tempOccupied', tempOccupied);
flow.set('f_tempUnOccupied', tempUnOccupied);

flow.set('f_schedulesAnalogValuesOffset',schedulesAnalogValuesOffset);
flow.set('f_deviceInstanceOffset',deviceInstanceOffset);
flow.set('f_deviceInstanceRange',deviceInstanceRange);
flow.set('f_deviceObjectInstanceNum',deviceObjectInstanceNum);
flow.set("f_generateScheduleAnalogValues", generateScheduleAnalogValues);


flow.set('f_timeZone', timeZone);
flow.set('f_dailySchedule', dailySchedule);
flow.set('f_timeOffsetBeforeStart', timeOffsetBeforeStart);
flow.set('f_timeOffsetBeforeEnd', timeOffsetBeforeEnd);
flow.set('f_contentRmEvent', contentRmEvent);
flow.set('f_preview', preview);
flow.set('f_pastview', pastview);
flow.set('f_sorted', sorted);

flow.set('f_rooms', rooms);

let rooms = flow.get("f_rooms");

flow.set('f_startTime', Date.now());
flow.set('f_nbRooms', rooms.length);
flow.set('f_roomCounter', 0);

// Send each room information as an individual msg
for (let j=0; j<rooms.length; j++) {
    msg.roomInfo = rooms[j];
    node.send(msg);
}

let roomInfo = msg.roomInfo;
let msgCorrect = false;

switch (msg.statusCode) {
    case 200:
        node.warn(`Successfully transmitted to the server. Process time = ${Date.now() - roomInfo.transmitTime} ms`);
        msgCorrect = true;
        break;
    case 400:
        node.error("Error 400: Bad HTTP Request");
        if (msg.payload.includes("write-access-denied")) {
            node.error("Error: Trying to write a Read Only object (analogInput)");
        }
        break;
    case 401:
        node.error("Error 401: Can't connect to controller: Authorization error");
        break;
    case 500:
        node.error("Error 500: Server Error 500");
        break;
    case 404:
        node.error("Error 404");
        break;
    case "ETIMEDOUT":
        node.error("Error: Can't connect to controller: TimeOut");
        break;
    case 501:
        node.error("Error 501. Not recognized request methode. Server not capable of supporting it for any resource")
        break;
    case "ENOTFOUND":
        node.error("RequestError: no url found. Please correct the url")
}

// Debug information
let nbRooms = flow.get('f_nbRooms');
flow.set('f_roomCounter', flow.get('f_roomCounter') + 1);
let roomCounter = flow.get('f_roomCounter');

if (roomCounter == nbRooms && msgCorrect) {
    node.warn(`All rooms processed in ${(Date.now() - flow.get('f_startTime')) / 1000} s`);
}
else if (!msgCorrect) {
    node.error("Schedule configuration not properly processed");
}

msg.dequeue = true;
return msg;
