    if (flow.get('g_dailySchedule')) {
        if (description.includes(flow.get('g_contentRmEvent')) && eventEnd.getHours() > 19) eventAlreadyExists = true; // Skip SOBRIETE ENERGETIQUE events
        else {
            let start = 0;
